// 每页显示的最大行数（两列布局时总行数）
let ROWS_PER_PAGE = 20;
let ROWS_PER_COLUMN = 20;

// 当前页码
let currentPage = 1;
let totalPages = 1;
let certificateData = [];

// 缩放相关变量
let currentZoom = 1;
const MIN_ZOOM = 0.5;
const MAX_ZOOM = 2;
const ZOOM_STEP = 0.1;

// 格式化日期
function formatDate(dateString) {
    if (!dateString) {
        const today = new Date();
        dateString = today.toISOString().split('T')[0];
    }
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = date.getMonth() + 1;
    const day = date.getDate();
    
    return `${year}年${month.toString().padStart(2, '0')}月${day.toString().padStart(2, '0')}日`;
}

// 更新页码信息
function updatePageInfo() {
    document.getElementById('currentPage').textContent = currentPage;
    document.getElementById('totalPages').textContent = totalPages;
    document.getElementById('prevPage').disabled = currentPage === 1;
    document.getElementById('nextPage').disabled = currentPage === totalPages;
}

// 显示指定页面
function showPage(pageNumber) {
    if (pageNumber < 1 || pageNumber > totalPages) return;
    
    document.querySelectorAll('.certificate-page').forEach(page => {
        page.classList.remove('active');
    });
    
    const targetPage = document.querySelector(`.certificate-page:nth-child(${pageNumber})`);
    if (targetPage) {
        targetPage.classList.add('active');
    currentPage = pageNumber;
        updatePaginationControls();
    }
}

// 上一页
function prevPage() {
    if (currentPage > 1) {
        showPage(currentPage - 1);
    }
}

// 下一页
function nextPage() {
    if (currentPage < totalPages) {
        showPage(currentPage + 1);
    }
}

// 生成证明题头
function generateHeader(studentCount, headerTemplate) {
    return headerTemplate.replace('x', studentCount);
}

// 创建表格HTML
function createTableHTML(data) {
    return `
        <thead>
            <tr>
                <th>姓名</th>
                <th>行政班级</th>
                <th>学号</th>
            </tr>
        </thead>
        <tbody>
            ${data.map(row => `
                <tr>
                    <td>${row['姓名'] || ''}</td>
                    <td>${row['行政班级'] || ''}</td>
                    <td>${row['学号'] || ''}</td>
                </tr>
            `).join('')}
        </tbody>
    `;
}

// 创建证明页面
function createCertificatePage(title, header, tableData, organization, date) {
    const page = document.createElement('div');
    page.className = 'certificate-page';
    
    // 添加标题
    const titleElement = document.createElement('h1');
    titleElement.textContent = title;
    page.appendChild(titleElement);
    
    // 添加题头
    const headerElement = document.createElement('div');
    headerElement.className = 'header';
    headerElement.textContent = header;
    page.appendChild(headerElement);
    
    // 创建表格容器
    const tablesContainer = document.createElement('div');
    tablesContainer.className = 'tables-container';
    
    // 判断是否需要两列布局
    if (tableData.length > ROWS_PER_COLUMN) {
        // 左列表格
        const leftColumn = document.createElement('div');
        leftColumn.className = 'table-column';
        const leftTable = document.createElement('table');
        leftTable.innerHTML = createTableHTML(tableData.slice(0, ROWS_PER_COLUMN));
        leftColumn.appendChild(leftTable);
        
        // 右列表格
        const rightColumn = document.createElement('div');
        rightColumn.className = 'table-column';
        const rightTable = document.createElement('table');
        rightTable.innerHTML = createTableHTML(tableData.slice(ROWS_PER_COLUMN));
        rightColumn.appendChild(rightTable);
        
        tablesContainer.appendChild(leftColumn);
        tablesContainer.appendChild(rightColumn);
    } else {
        // 单列表格
        const singleColumn = document.createElement('div');
        singleColumn.className = 'table-column';
        const table = document.createElement('table');
        table.innerHTML = createTableHTML(tableData);
        singleColumn.appendChild(table);
        tablesContainer.appendChild(singleColumn);
    }
    
    page.appendChild(tablesContainer);
    
    // 添加落款
    const footer = document.createElement('div');
    footer.className = 'footer';
    footer.innerHTML = `
        <p>${organization.replace(/\n/g, '<br>')}</p>
        <p>${formatDate(date)}</p>
    `;
    page.appendChild(footer);
    
    return page;
}

// 更新预览
function updatePreview() {
    // 更新标题
    document.getElementById('previewTitle').textContent = document.getElementById('title').value;
    
    // 更新题头
    const headerText = document.getElementById('header').value;
    const studentCount = document.querySelectorAll('#previewTable tbody tr').length;
    document.getElementById('previewHeader').textContent = headerText.replace('x', studentCount || 'x');
    
    // 更新落款
    document.getElementById('previewOrganization').textContent = document.getElementById('organization').value;
    const date = document.getElementById('date').value;
    document.getElementById('previewDate').textContent = formatDate(date);
}

// 创建表格行
function createTableRow(data) {
    return `
        <tr>
            <td>${data['姓名'] || ''}</td>
            <td>${data['行政班级'] || ''}</td>
            <td>${data['学号'] || ''}</td>
        </tr>
    `;
}

// 更新缩放级别显示
function updateZoomLevel() {
    const zoomPercent = Math.round(currentZoom * 100);
    document.querySelector('.zoom-level').textContent = `${zoomPercent}%`;
}

// 应用缩放
function applyZoom() {
    const pages = document.querySelectorAll('.certificate-page');
    pages.forEach(page => {
        page.style.transform = `scale(${currentZoom})`;
    });
}

// 缩放控制
function changeZoom(direction) {
    if (direction === 'in' && currentZoom < MAX_ZOOM) {
        currentZoom = Math.min(currentZoom + ZOOM_STEP, MAX_ZOOM);
    } else if (direction === 'out' && currentZoom > MIN_ZOOM) {
        currentZoom = Math.max(currentZoom - ZOOM_STEP, MIN_ZOOM);
    }
    updateZoomLevel();
    applyZoom();
}

// 重置缩放
function resetZoom() {
    currentZoom = 1;
    updateZoomLevel();
    applyZoom();
}

// 初始化页面时自动调整缩放以适应窗口
function initializeZoom() {
    const container = document.querySelector('.certificate-pages');
    const page = document.querySelector('.certificate-page');
    if (!container || !page) return;

    // 获取容器和页面的尺寸
    const containerHeight = container.clientHeight - 48; // 减去内边距
    const containerWidth = container.clientWidth - 48;
    const pageHeight = 297; // A4 高度（mm）
    const pageWidth = 210;  // A4 宽度（mm）

    // 计算合适的缩放比例
    const heightRatio = containerHeight / pageHeight;
    const widthRatio = containerWidth / pageWidth;
    currentZoom = Math.min(heightRatio, widthRatio, 1) * 0.95; // 95% 以确保有一些边距

    updateZoomLevel();
    applyZoom();
}

// 初始化页面
function initializePage() {
    // 创建初始预览
    createInitialPreview();
    
    // 设置事件监听器
    setupEventListeners();
    
    // 设置当前日期
    const today = new Date().toISOString().split('T')[0];
    document.getElementById('date').value = today;

    // 初始化缩放
    initializeZoom();

    // 检查移动设备
    checkMobileWarning();

    // 设置默认每页人数
    document.getElementById('rowsPerPage').value = ROWS_PER_PAGE;
}

// 初始化
document.addEventListener('DOMContentLoaded', initializePage);

// 添加窗口调整大小时的处理
window.addEventListener('resize', () => {
    initializeZoom();
    checkMobileWarning();
});

// 创建初始预览
function createInitialPreview() {
    const container = document.querySelector('.certificate-pages');
    const page = createEmptyPreviewPage();
    container.innerHTML = '';
    container.appendChild(page);
    page.classList.add('active');
}

// 创建空预览页面
function createEmptyPreviewPage() {
    const page = document.createElement('div');
    page.className = 'certificate-page';
    
    const title = document.getElementById('title').value || '活动证明';
    const headerText = document.getElementById('header').value.replace('x', '0');
    const organization = document.getElementById('organization').value;
    const date = document.getElementById('date').value;
    
    page.innerHTML = `
        <h1>${title}</h1>
        <div class="header">${headerText}</div>
        <div class="tables-container">
            <div class="table-column">
                <table>
                    <thead>
                        <tr>
                            <th>姓名</th>
                            <th>行政班级</th>
                            <th>学号</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td colspan="3" style="color: #999;">请导入数据</td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
        <div class="footer">
            <p>${organization.replace(/\n/g, '<br>')}</p>
            <p>${formatDate(date)}</p>
        </div>
    `;
    
    return page;
}

// 更新空预览
function updateEmptyPreview() {
    const pages = document.querySelectorAll('.certificate-page');
    if (!pages.length) return;
    
    const title = document.getElementById('title').value;
    const headerText = document.getElementById('header').value.replace('x', certificateData.length || '0');
    const organization = document.getElementById('organization').value;
    const date = document.getElementById('date').value;
    
    // 更新所有页面的内容
    pages.forEach(page => {
        page.querySelector('h1').textContent = title;
        page.querySelector('.header').textContent = headerText;
        page.querySelector('.footer').innerHTML = `
            <p>${organization.replace(/\n/g, '<br>')}</p>
            <p>${formatDate(date)}</p>
        `;
    });
}

// 自动调整文本框高度
function autoResizeTextarea(textarea) {
    textarea.style.height = 'auto';
    textarea.style.height = textarea.scrollHeight + 'px';
}

// 设置事件监听器
function setupEventListeners() {
    // 选择文件按钮
    document.getElementById('selectFileBtn').addEventListener('click', () => {
        document.getElementById('fileInput').click();
    });

    // 文件选择处理
    document.getElementById('fileInput').addEventListener('change', (event) => {
        const file = event.target.files[0];
        const fileName = document.getElementById('fileName');
        if (file) {
            fileName.textContent = file.name;
            fileName.style.color = '#52c41a';  // 设置为绿色
            document.getElementById('importBtn').disabled = false;
        } else {
            fileName.textContent = '未选择文件';
            fileName.style.color = '#666';  // 恢复默认颜色
            document.getElementById('importBtn').disabled = true;
        }
    });

    // 导入按钮
    document.getElementById('importBtn').addEventListener('click', importExcel);

    // 分页按钮事件监听
    document.getElementById('prevPage').addEventListener('click', () => {
        if (currentPage > 1) {
            currentPage--;
            updatePaginationControls();
        }
    });

    document.getElementById('nextPage').addEventListener('click', () => {
        if (currentPage < totalPages) {
            currentPage++;
            updatePaginationControls();
        }
    });

    // 自动调整文本框高度
    const textareas = ['header', 'organization'];
    textareas.forEach(id => {
        const textarea = document.getElementById(id);
        if (textarea) {
            // 初始化高度
            autoResizeTextarea(textarea);
            
            // 输入时调整高度
            textarea.addEventListener('input', () => {
                autoResizeTextarea(textarea);
            });
            
            // 改变大小时调整高度
            textarea.addEventListener('change', () => {
                autoResizeTextarea(textarea);
            });
        }
    });

    // 实时预览更新 - 使用防抖处理
    let updateTimeout;
    ['title', 'header', 'organization', 'date'].forEach(id => {
        const element = document.getElementById(id);
        if (element) {
            element.addEventListener('input', () => {
                clearTimeout(updateTimeout);
                updateTimeout = setTimeout(() => {
                    updateEmptyPreview();
                    if (element.tagName.toLowerCase() === 'textarea') {
                        autoResizeTextarea(element);
                    }
                }, 100);
            });
        }
    });
}

// 更新分页控制
function updatePaginationControls() {
    const currentPageSpan = document.getElementById('currentPage');
    const totalPagesSpan = document.getElementById('totalPages');
    const prevPageBtn = document.getElementById('prevPage');
    const nextPageBtn = document.getElementById('nextPage');
    
    if (currentPageSpan) currentPageSpan.textContent = currentPage;
    if (totalPagesSpan) totalPagesSpan.textContent = totalPages;
    if (prevPageBtn) prevPageBtn.disabled = currentPage <= 1;
    if (nextPageBtn) nextPageBtn.disabled = currentPage >= totalPages;
    
    // 更新页面显示
    document.querySelectorAll('.certificate-page').forEach((page, index) => {
        page.style.display = index + 1 === currentPage ? 'block' : 'none';
    });
}

// 打印证明
function printCertificates() {
    // 保存当前页面状态
    const currentPageNumber = currentPage;
    const pages = document.querySelectorAll('.certificate-page');
    
    // 临时移除分页控制
    document.querySelector('.pagination-controls').style.display = 'none';
    
    // 显示所有页面
    pages.forEach(page => {
        page.style.display = 'block';
        page.style.transform = 'none';
    });

    // 设置打印样式
    const style = document.createElement('style');
    style.textContent = `
        @media print {
            body { margin: 0; padding: 0; }
            .certificate-page {
                page-break-after: always;
                margin: 0;
                padding: 75.6px;
            }
            .certificate-page:last-child {
                page-break-after: avoid;
            }
        }
    `;
    document.head.appendChild(style);

    // 执行打印
    window.print();

    // 恢复页面状态
    setTimeout(() => {
        // 移除临时样式
        style.remove();
        
        // 恢复分页控制显示
        document.querySelector('.pagination-controls').style.display = 'flex';
        
        // 恢复页面显示状态
        pages.forEach((page, index) => {
            page.style.display = index + 1 === currentPageNumber ? 'block' : 'none';
            page.style.transform = `scale(${currentZoom})`;
        });
        
        // 恢复当前页码
        currentPage = currentPageNumber;
        updatePaginationControls();
    }, 100);
}

// 导入Excel文件
function importExcel() {
    const fileInput = document.getElementById('fileInput');
    const file = fileInput.files[0];
    const importBtn = document.getElementById('importBtn');
    const dataStatus = document.getElementById('dataStatus');
    const container = document.querySelector('.certificate-pages');
    const generateBtn = document.getElementById('generateBtn');
    const printBtn = document.querySelector('.print-btn');
    
    if (!file) {
        alert('请先选择文件');
        return;
    }
    
    // 禁用导入按钮并显示加载状态
    if (importBtn) importBtn.disabled = true;
    if (dataStatus) dataStatus.textContent = '正在导入数据...';
    
    const reader = new FileReader();
    reader.onload = function(e) {
        try {
            const data = new Uint8Array(e.target.result);
            const workbook = XLSX.read(data, {type: 'array'});
            const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
            let jsonData = XLSX.utils.sheet_to_json(firstSheet);
            
            if (jsonData.length === 0) {
                throw new Error('表格中没有数据');
            }

            // 对数据进行排序：先按班级排序，班级相同则按学号排序
            jsonData.sort((a, b) => {
                // 首先按班级排序
                const classCompare = String(a['行政班级'] || '').localeCompare(String(b['行政班级'] || ''), 'zh-CN');
                if (classCompare !== 0) {
                    return classCompare;
                }
                // 班级相同时按学号排序
                return String(a['学号'] || '').localeCompare(String(b['学号'] || ''), 'zh-CN');
            });
            
            // 更新数据状态
            certificateData = jsonData;
            if (dataStatus) {
                dataStatus.textContent = `已成功导入 ${jsonData.length} 条数据`;
                dataStatus.style.color = '#52c41a';
            }
            
            // 计算总页数
            totalPages = Math.ceil(jsonData.length / (ROWS_PER_PAGE * 2));
            currentPage = 1;
            
            // 生成所有页面
            if (container) {
                container.innerHTML = ''; // 清空现有内容
                
                for (let i = 0; i < totalPages; i++) {
                    const startIdx = i * ROWS_PER_PAGE * 2;
                    const endIdx = Math.min(startIdx + ROWS_PER_PAGE * 2, jsonData.length);
                    const pageData = jsonData.slice(startIdx, endIdx);
                    
                    const title = document.getElementById('title')?.value || '活动证明';
                    const headerText = document.getElementById('header')?.value.replace('x', jsonData.length) || '';
                    const organization = document.getElementById('organization')?.value || '';
                    const date = document.getElementById('date')?.value || '';
                    
                    const page = createCertificatePage(title, headerText, pageData, organization, date);
                    container.appendChild(page);
                }
            }
            
            // 显示第一页并更新分页控制
            document.querySelectorAll('.certificate-page').forEach((page, index) => {
                if (index === 0) {
                    page.classList.add('active');
                    page.style.display = 'block';
                } else {
                    page.style.display = 'none';
                }
            });
            
            // 更新分页控制和按钮状态
            updatePaginationControls();
            
            // 启用相关按钮
            if (generateBtn) generateBtn.disabled = false;
            if (printBtn) printBtn.disabled = false;
            
        } catch (error) {
            console.error('导入错误:', error);
            if (dataStatus) {
                dataStatus.textContent = '导入失败：' + (error.message || '文件格式错误');
                dataStatus.style.color = '#ff4d4f';
            }
            if (importBtn) importBtn.disabled = false;
        }
    };
    
    reader.onerror = function() {
        if (dataStatus) {
            dataStatus.textContent = '文件读取失败';
            dataStatus.style.color = '#ff4d4f';
        }
        if (importBtn) importBtn.disabled = false;
    };
    
    reader.readAsArrayBuffer(file);
}

