// 关闭移动设备提示
function closeMobileWarning() {
    const warning = document.getElementById('mobileWarning');
    if (warning) {
        warning.style.display = 'none';
        // 存储用户的选择，24小时内不再显示
        localStorage.setItem('mobileWarningClosed', Date.now());
    }
}

// 检查是否需要显示移动设备提示
function checkMobileWarning() {
    // 检查是否是移动设备
    if (window.innerWidth <= 768) {
        const lastClosed = localStorage.getItem('mobileWarningClosed');
        const now = Date.now();
        // 如果从未关闭过或者已经过了24小时
        if (!lastClosed || (now - lastClosed > 24 * 60 * 60 * 1000)) {
            const warning = document.getElementById('mobileWarning');
            if (warning) {
                warning.style.display = 'flex';
            }
        }
    }
}

// 窗口大小改变时检查
window.addEventListener('resize', () => {
    checkMobileWarning();
});

function showAbout() {
    document.getElementById('aboutModal').classList.add('active');
}

function closeAbout() {
    document.getElementById('aboutModal').classList.remove('active');
}

// 调整每页行数
function adjustRows(action) {
    const input = document.getElementById('rowsPerPage');
    let value = parseInt(input.value);
    
    if (action === 'increase') {
        value = Math.min(value + 1, 100);
    } else {
        value = Math.max(value - 1, 1);
    }
    
    updateRowsPerPage(value);
}

// 更新每页行数
function updateRowsPerPage(value) {
    const newValue = Math.max(1, Math.min(parseInt(value) || 20, 100));
    document.getElementById('rowsPerPage').value = newValue;
    
    // 更新行数变量
    ROWS_PER_PAGE = newValue;
    ROWS_PER_COLUMN = newValue;
    
    // 重新计算总页数和更新预览
    if (certificateData.length > 0) {
        const oldPage = currentPage;
        totalPages = Math.ceil(certificateData.length / (ROWS_PER_PAGE * 2));
        
        // 如果当前页超出新的总页数，则调整到最后一页
        currentPage = Math.min(oldPage, totalPages);
        
        // 重新生成所有页面
        const container = document.querySelector('.certificate-pages');
        if (container) {
            container.innerHTML = '';
            
            for (let i = 0; i < totalPages; i++) {
                const startIdx = i * ROWS_PER_PAGE * 2;
                const endIdx = Math.min(startIdx + ROWS_PER_PAGE * 2, certificateData.length);
                const pageData = certificateData.slice(startIdx, endIdx);
                
                const title = document.getElementById('title')?.value || '活动证明';
                const headerText = document.getElementById('header')?.value.replace('x', certificateData.length) || '';
                const organization = document.getElementById('organization')?.value || '';
                const date = document.getElementById('date')?.value || '';
                
                const page = createCertificatePage(title, headerText, pageData, organization, date);
                if (i + 1 === currentPage) {
                    page.classList.add('active');
                }
                container.appendChild(page);
            }
        }
        
        // 更新分页控制
        updatePaginationControls();
    }
} 